DNA_sequence1=''
DNA_sequence2=''
seq1_list = [nuc for nuc in DNA_sequence1]
seq2_list = [nuc for nuc in DNA_sequence2]

point_mutation = 0
i = 0

for chr in seq1_list:
    try:
        if chr != seq2_list[i]:
            point_mutation += 1
        i+= 1
    except:
        break

print(point_mutation)
